let credits = { 
    "student_names": "Paweł_MuchaA", 
    "tool_name": "MIND Visualization", 

"tool_info": "The mind is a structure. The mind is a chaos. The mind  is full. The mind is empty. The mind can be avant-garde or minimalist. The mind is what we feel. The mind is creativity. The mind is colorful. The mind contains thoughts and thoughts are jolts of electricity in our minds. The reason for my work is to show what's happening in the mind.                            Music I have used in video belongs to great Polish Dj: VTSS (Martyna Maja).Track: VTSS - Sober Raving [H311CAT01] (https://www.youtube.com/watch?v=18c5bO1adSg) BigLove to her for her music."

} 

let fc = 0
	px = 0
	py = 0


let font, points;
let opacitySlider
let noiseSlider
let sizeSlider;
let sizeTextSlider

let txt = "MIND"

let buttonExport;
let buttonRotate;
let checkboxRotate;
let checkboxHue;
let doHue = false;
let doSpin = false;


function preload() {
	font = loadFont("https://cdn.glitch.com/ec102b4b-0f5e-4f38-859a-4430679dda2f%2FKronaOne-Regular.ttf?v=1615503582411");
}




function setup() {

	createCanvas(windowWidth, windowHeight);

	opacitySlider = createSlider(0, 10, .05, 1)
	opacitySlider.position(width / 4, 25)

	noiseSlider = createSlider(0.001, .5, .005, .001);
	noiseSlider.position(width / 4, 50);

	sizeSlider = createSlider(100, 500, 100, 10);
	sizeSlider.position(width / 4, 75);

	sizeSlider2 = createSlider(100, 300, 100, 10);
	sizeSlider2.position(width / 4, 100);

	sizeTextSlider = createSlider(25, 500, 5, 1);
	sizeTextSlider.position(width /3, 100);


	//	buttonRotate = createButton('Spin');
	//	buttonRotate.position(width/3,30);
	//	buttonRotate.mousePressed(toggleSpin);

	buttonExport = createButton("EXPORT");
	buttonExport.position(width / 3, 10);
	buttonExport.mousePressed(exportImage);

	checkboxRotate = createCheckbox('SPIN', false);
	checkboxRotate.position(width / 3, 40);
	checkboxRotate.style('color', 'white')
	checkboxRotate.changed(myCheckedEvent);



	checkboxHue = createCheckbox('COLORS', false);
	checkboxHue.position(width / 3,70);
	checkboxHue.style('color', 'white')
	checkboxHue.changed(myCheckedEvent2);


//	noCursor();
}

function exportImage() {
	save('MindVisualisation_PawełMucha.png');

}
//function toggleSpin(){
//	doSpin = !doSpin;
//}

//}
//function toggleHue(){
//	doHue = !doHue;
//}

function myCheckedEvent() {
	if(checkboxRotate.checked()) {
		doSpin = true;
	} else {
		doSpin = false;
	}
}

function myCheckedEvent2() {
	if(checkboxHue.checked()) {
		doHue = true;
	} else {
		doHue = false;
	}

}

function draw() {

	//background(0,opacitySlider.value());

	//	genType(txt, sizeTextSlider.value());

	if(mouseX > width / 2) {
		stroke(255);
		background(0, opacitySlider.value());
	} else {
		stroke(0);
		background(255, opacitySlider.value());
	}

	let changeMeth = sin(fc * .800);
	let changeMeth2 = sin(fc * .04);
	let changeMeth4 = cos(fc * .8)
	let changeMeth3 = width / 4

	// ~ 6200 full rotation
	for(let i = 1; i < 520; i++) {
		fc += noiseSlider.value(); // 

		// function keyPressed (Space){
		// 	changeMeth = cos(fc * .800);
		// }

		if(mouseIsPressed) {
			changeMeth = cos(fc * .008);
			changeMeth2 = cos(fc * .20);
			changeMeth3 = width / 10
			changeMeth4 = sin(fc * .8)

		}

		let x = changeMeth * sin(fc * sizeSlider.value()) * changeMeth2 * changeMeth3
		let y = changeMeth4 * sin(fc * .04) * cos(fc * sizeSlider2.value()) * width / 4

		// function mousePressed(){
		// 	changeMeth = cos(fc * .800);}


		//ellipse(width / 2 + x, height / 2 + y, osc1 * 50)
		//stroke(225)
		strokeWeight(2 + abs(sin(fc * .1)) * abs(sin(fc * .02)) * 15)
		strokeWeight(2);
		if(px != 0) {
			push()
			translate(width / 2, height / 2);
			if(doSpin) {
				rotate(radians(frameCount * .5))
			}
			
			if(doHue) {
				stroke(random(255), random(255), random(255))
			//	stroke(random(0), random(255), random(0))
			}
			line(x, y, px, py);
			pop()

			push()
			//translate(width/2, height/2);
		
			//line(x, y, px, py);
			pop()

		}
		px = x
		py = y
	}


	push()
	if(mouseX > width / 2) {
		stroke(0);

	} else {
		stroke(255);

	}



	translate(width / 2, height / 2)
	noFill();
	//stroke(10*frameCount%255);
	strokeWeight(2);
	textAlign(CENTER, CENTER);
	textSize(sizeTextSlider.value());
	textFont('https://cdn.glitch.com/ec102b4b-0f5e-4f38-859a-4430679dda2f%2FKronaOne-Regular.ttf?v=1615503582411')
	text('MIND', random(-0.15, .015), random(-15, 15));

	pop()
	

	



}
