// let libs = ["https://cdn.jsdelivr.net/gh/bmoren/p5.collide2D/p5.collide2d.min.js"]

let butterfly;
let uImg;
let tImg;
let bImg;
let finger = [];
let sliderBackground, sliderSize;
let sliderSpeed, fc = 0;
let rx = 0, ry = 0, rsize = 50;

let letters = ["A", "B", "C", "D","E","F","G","H"]

var startOverSound;
var score = 0;
let uText = "✌"

//////////////////////////////////////////////////////////////////////
function preload() {
	uImg = loadImage('https://cdn.glitch.com/2073c31c-d8d5-4082-bd49-4f795e408c32%2Fclipart-purple-butterfly-128x128-75bd%20(1).png?v=1615389048861');
	tImg = loadImage('https://cdn.glitch.com/2073c31c-d8d5-4082-bd49-4f795e408c32%2Fclipart-pointing-finger-01-128x128-ff2f.png?v=1615417041427');
	startOverSound = loadSound("https://cdn.glitch.com/62b62d38-75d9-4bf9-a33f-2a99f1428973%2FES_Video%20Game%20Descend%201%20-%20SFX%20Producer.mp3?v=1615429583087");
}


/////////////////////////////////////////////////////////

let font = googleFont('Codystar');
let txt = '✧✦✧';

function setup() {
	createCanvas(windowWidth, windowHeight);
	butterfly = new Butterfly();
	textFont(font);
	textAlign(CENTER, CENTER);
	fill(255);
	fc = width / 2;

	sliderBackground = createSlider(0, 255, 128,60);
	sliderBackground.position(width / 2+500, 20);

	sliderSpeed = createSlider(0, 10, 1, .01);
	sliderSpeed.position(width/2+500 , 60);


}

function keyPressed() {
	if(key == ' ') {
		if(butterfly.gameOver) {
			butterfly.reset()
		} else {
			butterfly.jump();
		}
	}
}

function draw() {
	background(115, 235, sliderBackground.value(), 60);
	// background(bImg);
	//fc = fc + sliderSpeed.value();


	if(frameCount % 6==0){
		rx = random(width)
		ry = random(height)
		rsize = random(1, 100)
	}
	push()
	textSize(rsize);
	text(txt, rx, ry);
	pop()

	if(random(1) < 0.005) {
		finger.push(new Finger());
	}


	// function gameOn(){
	textAlign(CENTER);
	textSize(30);
	fill(255)
	text("SPACEBAR for JUMP and RESTART", width / 2, height / 9-40);

	textSize(60);
	fill(255)
	text("Score:" + score, width / 2, height / 6);

	for(let t of finger) {
		t.move();
		t.show();
		if(butterfly.hits(t)) {
			console.log('game over');
			butterfly.gameOver = true
			//write text game over here

		push()
			textSize(90);
	fill(255)
	text("Game Over", width / 2, height / 2);	
	pop()

			noLoop();
			startOverSound.play();
		}
	}

	butterfly.show();
	butterfly.move();

}

class Butterfly {
	constructor() {
		this.r = 150;
		this.x = 50;
		this.y = height - this.r;
		this.vy = 0;
		this.gravity = 3.5;

		this.gameOver = false

	}

	reset() {
		this.gameOver = false
		score = 0;
		finger = [];
		loop();
	}

	jump() {
		this.vy = -25
	}

	hits(train) {
		return collideRectRect(this.x, this.y, this.r, this.r, train.x + 50, train.y + 50, train.r, train.r);
	}

	move() {
		this.y += this.vy;
		this.vy += this.gravity;
		this.y = constrain(this.y, 0, height - this.r);
	}
	show() {
		push()
		textSize(this.r);
		text(uText, this.x, this.y, this.r, this.r);
		pop()
	}
}

class Finger {
	constructor() {
		this.r = 60;
		this.x = width;
		this.y = height - this.r;
		this.active = true
		this.letter = random(letters)
		this.textSize = random(50, 150)
		this.speed =  random(1,8 )
	}

	move() {
		if(this.active) {
			this.x -= this.speed + sliderSpeed.value(); // slider for speed???
			if(this.x < -60) {
				score += 1
				this.active = false
			}
		}
	}

	show() {
		// image(tImg, this.x, this.y, this.r, this.r);
		textSize(this.textSize)
		text(this.letter, this.x, this.y)
	}
}

function googleFont(fontName) {
	let link = document.createElement("link");
	link.href = "https://fonts.googleapis.com/css?family=" + encodeURI(fontName) + '&display=swap';
	link.rel = "stylesheet";
	document.head.appendChild(link);
	return fontName;
}
